INSERT INTO kb_articles (intent, question, answer, category, tags) VALUES
-- order_status (5)
('order_status', 'Where is my order?', 'You can check your order status by providing your order ID (format: ORD-XXXXX). We''ll look up the current status, tracking number, and estimated delivery date for you.', 'orders', ARRAY['tracking', 'delivery']),
('order_status', 'How long does shipping take?', 'Standard shipping takes 5-7 business days. Express shipping takes 2-3 business days. You can track your order using the tracking number provided in your shipping confirmation email.', 'orders', ARRAY['shipping', 'delivery', 'timeline']),
('order_status', 'My order is late', 'We apologize for the delay. Orders occasionally take longer during peak seasons. If your order is more than 3 days past the estimated delivery date, we can open an investigation with the carrier.', 'orders', ARRAY['delay', 'late', 'delivery']),
('order_status', 'Can I change my shipping address?', 'If your order has not yet shipped, we can update the shipping address. Once an order is in transit, address changes are not possible. Please provide your order ID and the new address.', 'orders', ARRAY['address', 'shipping', 'change']),
('order_status', 'I received the wrong item', 'We are sorry about that. Please provide your order ID and a description of the item you received. We will arrange a replacement shipment and provide a prepaid return label for the incorrect item.', 'orders', ARRAY['wrong item', 'replacement']),

-- return_request (5)
('return_request', 'How do I return an item?', 'To initiate a return, provide your order ID and the reason for the return. We will process your request and send a prepaid return label. Returns must be initiated within 30 days of delivery.', 'returns', ARRAY['return', 'refund', 'label']),
('return_request', 'What is your return policy?', 'We offer a 30-day return policy for most items. Items must be unused and in original packaging. Electronics have a 15-day return window. Final sale items cannot be returned.', 'returns', ARRAY['policy', 'return', 'window']),
('return_request', 'How long does a refund take?', 'Refunds are processed within 5-7 business days after we receive the returned item. The refund will appear on your original payment method. You will receive an email confirmation when the refund is issued.', 'returns', ARRAY['refund', 'timeline', 'payment']),
('return_request', 'Can I exchange instead of return?', 'Yes, we offer exchanges for the same item in a different size or color. Please initiate a return and place a new order for the desired item. We will waive shipping on the new order.', 'returns', ARRAY['exchange', 'swap', 'size']),
('return_request', 'My return was rejected', 'Returns may be rejected if the item is outside the return window, has been used, or is missing original packaging. If you believe this is an error, please provide your return ID and we will review your case.', 'returns', ARRAY['rejected', 'denied', 'return']),

-- product_info (5)
('product_info', 'Tell me about this product', 'I can look up detailed information about any product including description, price, availability, and specifications. Just provide the product name or SKU.', 'products', ARRAY['product', 'details', 'info']),
('product_info', 'Is this item in stock?', 'I can check real-time inventory for any product. Please provide the product name or SKU and I will confirm availability and estimated restock dates if needed.', 'products', ARRAY['stock', 'availability', 'inventory']),
('product_info', 'What are the product specifications?', 'Each product page includes full specifications. I can look up dimensions, materials, weight, and technical specifications for any item in our catalog.', 'products', ARRAY['specs', 'dimensions', 'technical']),
('product_info', 'Do you have any recommendations?', 'I can help you find products based on your needs. Tell me what you are looking for (category, budget, features) and I will suggest options from our catalog.', 'products', ARRAY['recommendations', 'suggest', 'help']),
('product_info', 'Compare two products', 'I can compare products side by side. Provide the names or SKUs of the products you want to compare and I will list the key differences in features, price, and specifications.', 'products', ARRAY['compare', 'versus', 'difference']),

-- shipping_info (5)
('shipping_info', 'What shipping options do you offer?', 'We offer Standard (5-7 business days, free over $50), Express (2-3 business days, $9.99), and Next Day ($19.99). All orders include tracking.', 'shipping', ARRAY['options', 'methods', 'speed']),
('shipping_info', 'Do you ship internationally?', 'We currently ship to the US and Canada. International shipping to Canada takes 7-14 business days. Customs duties may apply and are the responsibility of the recipient.', 'shipping', ARRAY['international', 'canada', 'global']),
('shipping_info', 'Free shipping threshold', 'Orders over $50 qualify for free standard shipping. Express and Next Day shipping have flat fees regardless of order total.', 'shipping', ARRAY['free', 'threshold', 'minimum']),
('shipping_info', 'My tracking number is not working', 'Tracking numbers may take 24-48 hours to activate after shipping. If your tracking number still shows no results after 48 hours, please provide your order ID and we will investigate.', 'shipping', ARRAY['tracking', 'not working', 'broken']),
('shipping_info', 'Can I pick up my order?', 'We do not currently offer in-store pickup. All orders are shipped directly to the provided address.', 'shipping', ARRAY['pickup', 'store', 'collect']),

-- payment_issue (5)
('payment_issue', 'My payment was declined', 'Payment declines can occur due to insufficient funds, incorrect card details, or bank security holds. Please verify your card information and try again, or use a different payment method.', 'payment', ARRAY['declined', 'failed', 'card']),
('payment_issue', 'What payment methods do you accept?', 'We accept Visa, Mastercard, American Express, Discover, PayPal, and Apple Pay. All transactions are secured with 256-bit encryption.', 'payment', ARRAY['methods', 'credit card', 'paypal']),
('payment_issue', 'I was charged twice', 'Double charges usually appear as a pending authorization and the actual charge. The pending authorization should drop off within 3-5 business days. If both charges are posted, please provide your order ID.', 'payment', ARRAY['double charge', 'duplicate', 'billing']),
('payment_issue', 'Can I use a discount code?', 'Discount codes can be entered at checkout in the promo code field. Only one code can be used per order. Some exclusions may apply to certain products or categories.', 'payment', ARRAY['discount', 'promo', 'coupon']),
('payment_issue', 'I need an invoice', 'We can send a detailed invoice for any order. Please provide your order ID and the email address where you would like the invoice sent.', 'payment', ARRAY['invoice', 'receipt', 'billing']),

-- account_help (5)
('account_help', 'How do I create an account?', 'You can create an account on our website by clicking Sign Up and providing your email address and a password. Account creation is optional for purchases.', 'account', ARRAY['create', 'register', 'signup']),
('account_help', 'I forgot my password', 'Click Forgot Password on the login page and enter your email. You will receive a password reset link within a few minutes. The link expires in 24 hours.', 'account', ARRAY['password', 'reset', 'forgot']),
('account_help', 'Update my email address', 'You can update your email address in your account settings under Profile. You will need to verify the new email address before the change takes effect.', 'account', ARRAY['email', 'update', 'change']),
('account_help', 'Delete my account', 'To delete your account, go to Settings and select Delete Account. This action is permanent and will remove all your data including order history. Active orders must be completed first.', 'account', ARRAY['delete', 'remove', 'close']),
('account_help', 'How do I view my order history?', 'Log into your account and go to My Orders. You can see all past orders, their statuses, and tracking information. Guest orders can be looked up using your email and order ID.', 'account', ARRAY['history', 'past orders', 'view']),

-- complaint (5)
('complaint', 'Product quality issue', 'We take quality seriously. Please provide your order ID and photos of the issue. We will either replace the item at no charge or issue a full refund.', 'complaints', ARRAY['quality', 'defective', 'broken']),
('complaint', 'Poor customer service experience', 'We apologize for your experience. Your feedback helps us improve. Can you share the details of what happened so we can address the issue and ensure it does not happen again?', 'complaints', ARRAY['service', 'experience', 'feedback']),
